import "../styles/studentprofile.css";
import { useEffect, useState } from "react";
import axios from 'axios';

function StudentProfile() {
    const [studentData, setStudentData] = useState({
        firstName: "",
        lastName: "",
        email: "",
        rollNumber: "",
        department: "",
        address: "",
        city: "",
        semester: "",
        cgpa: ""
    });

    useEffect(() => {
        const fetchStudentData = async () => {
            try {
                const res = await axios.get("http://localhost:8800/profile");
                const student = res.data.find(student => student.rollNumber === window.localStorage.getItem("userName"));
                if (student) {
                    setStudentData(student);
                }
            } catch (err) {
                console.log(err);
            }
        };
        fetchStudentData();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudentData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8800/students/${studentData.rollNumber}`, studentData);
            console.log("Profile updated successfully!");
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div id="studentP-container">
            <div id="studentP">
                <h1>Profile</h1>
                <div className="form">
                    <label>First Name: </label>
                    <input type="text" name="firstName" placeholder="First Name" value={studentData.firstName} onChange={handleChange} />
                    <label>Last Name: </label>
                    <input type="text" name="lastName" placeholder="Last Name" value={studentData.lastName} onChange={handleChange} />
                    <label>Roll Number: </label>
                    <input type="text" name="rollNumber" placeholder="Roll Number" value={studentData.rollNumber} disabled />
                    {/* Add other input fields similarly */}
                    <div className="btnHolder">
                        <button onClick={handleClick}>Update Profile!</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default StudentProfile;
