import React, { useState, useEffect } from "react";
import axios from "axios";

function AdmissionPortal() {
  const [loginData, setLoginData] = useState([]);
  const [error, setError] = useState(null); // State to hold error information

  useEffect(() => {
    const fetchAllCredentials = async () => {
      try {
        const res = await axios.get("http://localhost:8800/applicants");
        setLoginData(res.data);
        console.log(res.data); // Log the data received from the backend
      } catch (err) {
        setError(err.message); // Set error state with error message
        console.error(err); // Log any errors to the console
      }
    };
    fetchAllCredentials();
  }, []);

  if (error) {
    return <div>Error: {error}</div>; // Display error message if there's an error
  }

  return (
    <div id="admissionP-container">
      <div id="admissionP">
        <h1>Applications</h1>
        <table>
          <thead>
            <tr>
              <th>Roll Number</th>
              <th>Email</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Department</th>
              <th>Semester</th>
              <th>City</th>
              <th>Address</th>
              <th>CGPA</th>
            </tr>
          </thead>
          <tbody>
  {loginData.map((application, index) => (
    <tr key={`applicant_${index}`}>
      <td>{application.RollNumber}</td>
      <td>{application.Email}</td>
      <td>{application.FirstName}</td>
      <td>{application.LastName}</td>
      <td>{application.Department}</td>
      <td>{application.Semester}</td>
      <td>{application.City}</td>
      <td>{application.Address}</td>
      <td>{application.CGPA}</td>
    </tr>
  ))}
</tbody>

        </table>
      </div>
    </div>
  );
}

export default AdmissionPortal;
